export declare const animeGraphql = "\n    query ($search: String, $type: MediaType, $isAdult: Boolean) {\n        anime: Page (perPage: 5) {\n            results: media (type: $type, isAdult: $isAdult, search: $search) {\n                id\n                    idMal\n                title {\n                    english\n                    romaji\n                }\n                coverImage {\n                large\n                medium\n            }\n            startDate { day month year }\n            description(asHtml: false)\n            season\n            duration\n            genres\n            format\n            type\n            siteUrl\n            status\n            trailer {\n                id\n                site\n            }\n            episodes\n            isAdult\n            meanScore\n            averageScore\n            }\n        }\n    }\n";
export declare const mangaGraphql = "\nquery ($search: String, $type: MediaType, $isAdult: Boolean) {\n    anime: Page (perPage: 5) {\n        results: media (type: $type, isAdult: $isAdult, search: $search) {\n            id\n      \t\t\tidMal\n            title {\n                english\n                romaji\n            }\n            coverImage {\n            large\n            medium\n        }\n        startDate { day month year }\n        description(asHtml: false)        \n      \tgenres\n        format\n        type\n        siteUrl\n        status\n        trailer {\n            id\n            site\n        }\n        chapters\n      \tvolumes\n        isAdult\n        meanScore\n      \taverageScore\n        }\n    }\n}\n";
export declare const charGraphql = "\nquery ($search: String) {\n    characters: Page (perPage: 5) {\n        results: characters (search: $search) {\n            id\n            name {\n                full\n                first\n                last\n            }\n            image {\n                large\n                medium\n            }\n            description(asHtml: false)\n            siteUrl\n            media(page: 1, perPage: 5) {\n                edges {\n                    node {\n                        title {\n                            english\n                            userPreferred\n                        }\n                        type\n                        siteUrl\n                    }\n                }\n            }\n        }\n    }\n}\n";
export declare const voiceActors = "\nquery($id : Int){\n    Media(id: $id) {\n      characters(page: 1) {\n        edges {\n          node {\n            siteUrl\n            name {\n              full\n            }\n          }\n          role\n          voiceActors (language: JAPANESE) {\n            siteUrl\n            name {\n              full\n            }\n          }\n        }\n      }\n    }\n  }\n";
